function carbonFromIsoDate(string $date): Carbon\Carbon
{
    return Carbon\Carbon::createFromFormat(DATE_FORMAT_ISO, $date);
}

/**

/**
 * Get Carbon istance by string $date in 'd/m/Y H:i:s' format.
