 */
function attre($value)
{
    // pre-empt replacement
    if ($value === '' || ctype_digit($value)) {
        return $value;
    }

/**
 * Escape Shell argument.
 * @param string $input
 * @return string
 */
function she(string $input): string
{
    if (windows_os()) {
        return '"' . addcslashes($input, '\\"') . '"';
    }

