/**

 *
 * @param array $data
 *
 * @return array
 */
function sanitize_arr_string_xss(array $data): array
{
    foreach ($data as $k => $v) {
        $data[$k] = filter_var($v, FILTER_SANITIZE_STRING);
    }
    return $data;

