 */
function isInteger($value, $unsigned = true, $acceptIntegerFloatingPoints = false): bool
{
    if (isStringNumberStartsWithMoreThanOneZero($value)) {
        return false;
    }

 * Ex.: 1, 1e2, 1E2, 1e+2, 1e-2, 1.4e+2, -1.2e+2, -1.231e-2 etc...
